INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (72970, 0, 0, 'The eons have awakened me.', 14, 0, 100, 0, 0, 0, 'Golganarr spawn');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (73461, 0, 0, '"Though blood floods the ground, the mountain is unmoved." - Niuzao', 41, 0, 100, 0, 0, 0, 'Niuzao blessing');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (73461, 1, 0, '"The truth shines brightest in clouded times." - Yu`lon', 41, 0, 100, 0, 0, 0, 'Yu`lon blessing');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (73461, 2, 0, '"Hope`s fire grows when the flames come together." - Chi-Ji', 41, 0, 100, 0, 0, 0, 'Chi-Ji blessing');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (73461, 3, 0, '"Strength and power are as different as sun and moon." - Xuen', 41, 0, 100, 0, 0, 0, 'Xuen blessing');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (73461, 4, 0, '"The shrine was corrupted by Ordos!"', 41, 0, 100, 0, 0, 0, 'Ordos corruption');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (71952, 0, 0, 'Then let us begin!', 14, 0, 100, 0, 0, 0, 'Chi-Ji Aggro');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (71952, 1, 0, 'Believe in one another, and let others carry hope for you.', 14, 0, 100, 0, 0, 0, 'Chi-Ji Beacon of Hope');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (71952, 2, 0, 'Without hope, there is no brightness in the future.', 14, 0, 100, 0, 0, 0, 'Chi-Ji Crane Rush');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (71952, 2, 1, 'Create the destiny you seek.', 14, 0, 100, 0, 0, 0, 'Chi-Ji Crane Rush');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (71952, 3, 0, 'Do not give up on yourself!', 14, 0, 100, 0, 0, 0, 'Chi-Ji Kill Player');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (71952, 4, 0, 'Your hope shines brightly, and even more brightly when you work together to overcome. It will ever light your way, in even the darkest of places.', 14, 0, 100, 0, 0, 0, 'Chi-Ji Defeated');
INSERT INTO `creature_text` (`entry`, `groupid`, `id`, `text`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `comment`) VALUES (73303, 0, 0, 'You have walked the trial of hope, and learned of the path of the red crane. May it guide your footsteps through time.', 14, 0, 100, 0, 0, 0, 'Chi-Ji Defeated');